function expandCollapse(a){$(a).next().toggle("normal");$(a).parent().toggleClass("personal_info_record_selected")}function expandAll(){$(".personal_info_record > div").show();$(".personal_info_record").addClass("personal_info_record_selected")}function collapseAll(){$(".personal_info_record > div").hide();$(".personal_info_record").removeClass("personal_info_record_selected")}function selectSection(a){if($(a).parent().hasClass("personal_info_record_selected")){expandCollapse(a)}else{$(".personal_info_record_selected > div").hide();$(".personal_info_record_selected").removeClass("personal_info_record_selected");expandCollapse(a)}};